mapper.select:


	 and id = #{id} 
	 and code = #{code} 
	 and pay_type = #{payType} 
	 and pay_amount = #{payAmount} 
	 and order_source = #{orderSource} 
	 and drawee = #{drawee} 
	 and order_pay_time = #{orderPayTime} 
	 and order_code = #{orderCode} 
	 and create_time = #{createTime} 
	 and create_by = #{createBy} 
	 and update_time = #{updateTime} 
	 and update_by = #{updateBy} 

order by

	 id desc 
	 code desc 
	 pay_type desc 
	 pay_amount desc 
	 order_source desc 
	 drawee desc 
	 order_pay_time desc 
	 order_code desc 
	 create_time desc 
	 create_by desc 
	 update_time desc 
	 update_by desc 
	 id desc
