mapper.select:


	 and id = #{id} 
	 and red_rate = #{redRate} 
	 and red_parity_rate = #{redParityRate} 
	 and red_range_rate = #{redRangeRate} 
	 and red_sum_rate = #{redSumRate} 
	 and consecutive_numbers_count_rate = #{consecutiveNumbersCountRate} 
	 and max_consecutive_numbers_rate = #{maxConsecutiveNumbersRate} 
	 and type = #{type} 

order by

	 id desc 
	 red_rate desc 
	 red_parity_rate desc 
	 red_range_rate desc 
	 red_sum_rate desc 
	 consecutive_numbers_count_rate desc 
	 max_consecutive_numbers_rate desc 
	 type desc 
	 id desc
